	"github.com/pulumi/pulumi-pagerduty/sdk/go/pagerduty"
	"github.com/pulumi/pulumi/sdk/v2/go/pulumi"
)

func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		fooTeam, err := pagerduty.NewTeam(ctx, "fooTeam", nil)
		if err != nil {
