		}
		_, err = pagerduty.NewRuleset(ctx, "fooRuleset", &pagerduty.RulesetArgs{
			Team: &pagerduty.RulesetTeamArgs{
				Id: fooTeam.ID(),
			},
		})
		if err != nil {
			return err
		}
		return nil
	})
}
